	if( instance ){
	    if( target.checked ){
		mode = "visible";
	    } else {
		mode = "hidden";
	    }
	}
	// the plugin container contains the plugin and maybe a toolbar
	instance.divjq.closest(".JS9PluginContainer").css("visibility", mode);
