JS9.allinone.loadCorsHTML = "<form id=\"sharedURLForm\" name=\"sharedURLForm\" class=\"js9AnalysisForm\" action=\"\"><b>Dropbox allows you to share files using public links that can be given to others. These links support <a href=\"https://en.wikipedia.org/wiki/Cross-origin_resource_sharing\"> Cross-Origin Resource Sharing</a>, so JS9 can display them directly. Enter a Dropbox shared link to a FITS file below:</b><p><input type=\"text\" class=\"js9TextInput\" id=\"sharedURL\" name=\"sharedURL\" size=\"80\" autocapitalize=\"off\" autocorrect=\"off\"><p><input id=\"run\" name=\"run\" type=\"button\" class=\"button\" value=\"Load\" onclick=\"var arr = $('#sharedURLForm').serializeArray();var form = $(this).closest('form');var dhtml = form.closest('.dhtmlwindow')[0];var dispid = $(dhtml).data('dispid');var url = arr[0].value;if( url.match(/dropbox\.com/) ){  // http://stackoverflow.com/questions/20757891/cross-origin-image-load-from-cross-enabled-site-is-denied  url = url.replace('www.dropbox.com', 'dl.dropboxusercontent.com')  // https://blogs.dropbox.com/developers/2013/08/programmatically-download-content-from-share-links/  url = url.replace('?dl=0', '').trim() + '?raw=1';} else if( url.match(/drive\.google\.com/) ){  url=url.replace(/\/file\/d\/(\w+)\/\w+\?usp=sharing/,                  '/uc?export=download&amp;id=$1').trim();  JS9.error('Google Drive does not support CORS');}JS9.Load(url, {display: dispid});dhtml.close();return false;\"><input id=\"close\" name=\"Close\" type=\"button\" class=\"button\" value=\"Close\" onclick=\"var form = $(this).closest('form');var dhtml = form.closest('.dhtmlwindow')[0];dhtml.close();return false;\" style=\"float: right;\"></form>";
JS9.allinone.datapathHTML = "<form id=\"dataPathForm\" name=\"dataPathForm\" class=\"js9AnalysisForm\" action=\"\"><b>The Data Path is a colon-separated list of directories to search when performing local analysis on your drag and drop files:</b><p><textarea id=\"dataPath\" name=\"dataPath\" rows=\"2\" cols=\"100\" style=\"width:90%;\"></textarea><p><input id=\"run\" name=\"run\" type=\"button\" class=\"button\" value=\"Submit\" onclick=\"var arr = $('#dataPathForm').serializeArray();var form = $(this).closest('form');var dhtml = form.closest('.dhtmlwindow')[0];var dispid = $(dhtml).data('dispid');JS9.globalOpts.dataPath = arr[0].value;JS9.DisplayImage('display', {display: dispid});dhtml.close();return false;\"><input id=\"close\" name=\"Close\" type=\"button\" class=\"button\" value=\"Close\" onclick=\"var form = $(this).closest('form');var dhtml = form.closest('.dhtmlwindow')[0];dhtml.close();return false;\" style=\"float: right;\"></form>";
